import React, { useState, useEffect } from 'react';
import {Container, AppBar, Typography, Grow, Grid } from '@material-ui/core';
import {useDispatch} from 'react-redux';

import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';
import { getPosts } from './actions/posts';
import useStyles from './styles';
import Doggo from './images/Doggo.png';

//this should allow us to run our client side
const App = () => {
    const [currentId, setCurrentId] = useState(null);
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getPosts());
      }, [currentId, dispatch]);

    return (
       <Container maxidth = "lg">
            <AppBar className = {classes.appBar}  position = "static" color = "inherit">
                <Typography className = {classes.heading} variant = "h2" align = "center" color = "black">Pet Info</Typography>
                <img className ={classes.image} src = {Doggo} alt = "Doggo" height = "400" width = "35%" />
            </AppBar>
            <Grow in> 
                <Container>
                <Grid container justify="space-between" alignItems="stretch" spacing={3}>
            <Grid item xs={12} sm={7}>
              <Posts setCurrentId={setCurrentId} />
            </Grid>
            <Grid item xs={12} sm={4}>
              <Form currentId={currentId} setCurrentId={setCurrentId} />
            </Grid>
          </Grid>
        </Container>
      </Grow>
    </Container>
  );

};

export default App;
